import HttpService from './http.service';

const END_POINT = 'user';

export default {
  query,
  save,
  remove,
  getById,
};

function query(filter = null) {
  console.log('userService.BASE_URL', END_POINT);
  const users = HttpService.get(END_POINT, filter);
  return users;
}

function save(user) {
  if (user._id) {
    return HttpService.put(`${END_POINT}/${user._id}`, user);
  }
  user._id = _makeId(10);
  return HttpService.post(`${BASE_URL}`, user);
}

function remove(userID) {
  return HttpService.delete(`${BASE_URL}/${userID}`);
}

function getById(userId) {
  return HttpService.get(`${BASE_URL}/${userId}`);
}

function _makeId(length = 3) {
  let text = '';
  const possible = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';

  for (let i = 0; i < length; i++) {
    text += possible.charAt(Math.floor(Math.random() * possible.length));
  }
  return text;
}
