import React, { Component } from 'react';
import { connect } from 'react-redux';
import Rating from 'react-rating'

//Images
import star from '../img/star.svg'
import star_o from '../img/‏‏star-o.svg'


class _ReviewAdd extends Component {
    state = {
        name:'',
        rating: 1,
        review

    }



    render() {
        const { rating, name, review } = this.state
        return (
            <section className="reviewAdd">
                <Rating start={0}
                    stop={5}
                    initialRating={rating}
                    emptySymbol={<img src={star} width="30" />}
                    fullSymbol={<img src={star_o} width="30" />}
                    onChange={(rate) => { this.setState({ rating: { rate } }) }}
                />
                <form>
                    <input type="input" name="name" value={name} onChange={this.handledChange} placeholder="Name" />
                    <textarea name="review" name="review" value={review} cols="30" rows="10"></textarea>
                </form>
            </section>
        )
    }
}

// const mapStateToProps = state => {
//     return {

//     };
//   };
//   const mapDispatchToProps = {

//   };
export const ReviewAdd = connect()(_ReviewAdd);
